// Extract employees from the response
const response = $input.first().json;
const employees = response.employees || [];
const paging = response.paging || {};
const links = paging._links || {};
const nextLink = links.next || null;

// Parse the 'after' cursor from next link
let afterCursor = '';
if (nextLink) {
  try {
    const url = new URL(nextLink, 'https://api.lookc.io');
    afterCursor = url.searchParams.get('after') || '';
  } catch (e) {
    // If it's a relative URL
    const match = nextLink.match(/[?&]after=([^&]+)/);
    if (match) {
      afterCursor = match[1];
    }
  }
}

// Get current state from previous node
const currentState = $('Set Variables').first().json;
const pageCount = parseInt(currentState.page_count || 0) + 1;
const totalEmployees = parseInt(currentState.total_employees || 0) + employees.length;

// Return employees with metadata
return employees.map(employee => ({
  json: {
    ...employee,
    _metadata: {
      org_id: currentState.org_id,
      api_key: currentState.api_key,
      base_url: currentState.base_url,
      after_cursor: afterCursor,
      has_more: !!afterCursor,
      page_count: pageCount,
      total_employees: totalEmployees
    }
  }
}));

// Process each employee and prepare for database insertion
const items = $input.all();
const processedItems = [];

for (const item of items) {
  const employee = item.json;
  const metadata = employee._metadata;

  // Extract fields
  const personId = employee.personId;
  const orgId = metadata.org_id;
  const name = employee.name || null;
  const title = employee.title || null;
  const dateStarted = employee.dateStarted || null;
  const dateEnded = employee.dateEnded || null;
  const location = employee.location || null;
  const image = employee.image || null;

  const links = employee.links || {};
  const linksLinkedin = links.linkedin || null;
  const linksSalesNav = links.salesNav || null;

  const currentOrg = employee.currentOrg || {};
  let currentOrgId = currentOrg.orgId || null;
  const currentOrgName = currentOrg.name || null;

  // If no current org but not ended, assume it's the org we're querying
  if (!currentOrgId && !dateEnded) {
    currentOrgId = orgId;
  }

  // Convert tenure to months
  const toMonths = (duration) => {
    if (!duration) return null;
    const years = duration.years || 0;
    const months = duration.months || 0;
    return years * 12 + months;
  };

  const tenureAtOrgMonths = toMonths(employee.tenureAtOrg);
  const tenureInRoleMonths = toMonths(employee.tenureInRole);

  // Store full JSON data
  const jsonData = JSON.stringify(employee);
  const collectedAt = new Date().toISOString();

  processedItems.push({
    json: {
      person_id: personId,
      org_id: orgId,
      name: name,
      title: title,
      date_started: dateStarted,
      date_ended: dateEnded,
      location: location,
      image: image,
      current_org_id: currentOrgId,
      current_org_name: currentOrgName,
      links_linkedin: linksLinkedin,
      links_sales_nav: linksSalesNav,
      tenure_at_org_months: tenureAtOrgMonths,
      tenure_in_role_months: tenureInRoleMonths,
      data: jsonData,
      collected_at: collectedAt,
      _metadata: metadata
    }
  });
}

return processedItems;

// Get the last item's metadata to check if there are more pages
const items = $input.all();
if (items.length === 0) {
  return [{ json: { has_more: false } }];
}

const lastItem = items[items.length - 1];
const metadata = lastItem.json._metadata;

return [{
  json: {
    has_more: metadata.has_more,
    after_cursor: metadata.after_cursor,
    org_id: metadata.org_id,
    api_key: metadata.api_key,
    base_url: metadata.base_url,
    page_count: metadata.page_count,
    total_employees: metadata.total_employees
  }
}];

const item = $input.first().json;
return [{
  json: {
    message: 'Collection complete!',
    org_id: item.org_id,
    total_pages: item.page_count,
    total_employees: item.total_employees
  }
}];

return [{
  json: {
    message: 'Already collected all employees for this organization',
    org_id: $('Set Variables').first().json.org_id
  }
}];
